import React from 'react'
import { StyledButton } from './StyledButton'
import { StyledBox } from '../styledBox/StyledBox'

export const Button = props => {
  const {
    toggleButton,
    buttonColor,
    buttonWidth,
    buttonStyle,
    className,
    children,
    Box,
  } = props

  return (
    <StyledButton
      onClick={toggleButton}
      {...{ buttonColor, buttonStyle, className }}
    >
      <Box {...{ buttonWidth }}>{children}</Box>
    </StyledButton>
  )
}

Button.defaultProps = {
  buttonWidth: '40px',
  Box: StyledBox,
}
