import React from 'react'
import { Button } from '../button/Button'
import { StyledLinesArrow } from './StyledLinesArrow'

const HamburgerArrow = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledLinesArrow {...{ isActive, barColor }} />
    </Button>
  )
}

export default HamburgerArrow
