import React from 'react'

import Button from '../Button/Button'
import Box from '../Box/Box'
import LinesArrow from './LinesArrow'

const HamburgerArrow = props => (
  <Button onClick={props.toggleButton} buttonColor={props.buttonColor}>
    <Box>
      <LinesArrow isActive={props.isActive} barColor={props.barColor} />
    </Box>
  </Button>
)

export default HamburgerArrow
