import { StyledLines } from '../styledLines/StyledLines'
import { getBarColor } from '../functions/getBarColor'

export const StyledLinesArrow = StyledLines.extend`
  &::before {
    ${({ isActive }) =>
      isActive
        ? 'transform: translate3d(-8px, 0, 0) rotate(-45deg) scale(0.7, 1)'
        : ''};
    ${getBarColor};
  }
  &::after {
    ${({ isActive }) =>
      isActive
        ? 'transform: translate3d(-8px, 0, 0) rotate(45deg) scale(0.7, 1)'
        : ''};
    ${getBarColor};
  }
  ${getBarColor};
`

StyledLinesArrow.displayName = 'StyledLinesArrow'
