import React from 'react'

import Button from '../Button/Button'
import Box from '../Box/Box'
import LinesArrowAlt from './LinesArrowAlt'

const HamburgerArrowAlt = props => (
  <Button onClick={props.toggleButton} buttonColor={props.buttonColor}>
    <Box>
      <LinesArrowAlt isActive={props.isActive} barColor={props.barColor} />
    </Box>
  </Button>
)

export default HamburgerArrowAlt
