import React from 'react'
import { Button } from '../button/Button'
import { StyledLinesArrowAlt } from './StyledLinesArrowAlt'

const HamburgerArrowAlt = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledLinesArrowAlt {...{ isActive, barColor }} />
    </Button>
  )
}

export default HamburgerArrowAlt
