import { StyledLines } from '../styledLines/StyledLines'
import { getBarColor } from '../functions/'

const IsActiveBefore = `
  top: 0;
  transform: translate3d(-8px, -10px, 0) rotate(-45deg) scale(0.7, 1);
  transition: top 0.1s ease, transform 0.1s 0.1s cubic-bezier(0.895, 0.03, 0.685, 0.22);
`
const IsActiveAfter = `
  bottom: 0;
  transform: translate3d(-8px, 10px, 0) rotate(45deg) scale(0.7, 1);
  transition: bottom 0.1s ease, transform 0.1s 0.1s cubic-bezier(0.895, 0.03, 0.685, 0.22);
`

export const StyledLinesArrowAlt = StyledLines.extend`
  &::before {
    transition: top 0.1s 0.1s ease,
      transform 0.1s cubic-bezier(0.165, 0.84, 0.44, 1);
    ${({ isActive }) => isActive && IsActiveBefore};
    ${getBarColor};
  }
  &::after {
    transition: bottom 0.1s 0.1s ease,
      transform 0.1s cubic-bezier(0.165, 0.84, 0.44, 1);
    ${({ isActive }) => isActive && IsActiveAfter};
    ${getBarColor};
  }
  ${getBarColor};
`

StyledLinesArrowAlt.displayName = 'StyledLinesArrowAlt'
