import Lines from '../Lines/Lines'

const LinesArrowTurn = Lines.extend`
  &::before {
    ${props =>
      props.isActive
        ? 'transform: translate3d(8px, 0, 0) rotate(45deg) scale(0.7, 1)'
        : ''};
    background-color: ${props => props.barColor || 'black'};
  }
  &::after {
    ${props =>
      props.isActive
        ? 'transform: translate3d(8px, 0, 0) rotate(-45deg) scale(0.7, 1)'
        : ''};
    background-color: ${props => props.barColor || 'black'};
  }
  ${props => (props.isActive ? 'transform: rotate(-180deg)' : '')};
  background-color: ${props => props.barColor || 'black'};
`

export default LinesArrowTurn
