import { StyledLines } from '../styledLines/StyledLines'
import { getBarColor } from '../functions/'

export const StyledLinesArrowTurn = StyledLines.extend`
  &::before {
    ${props =>
      props.isActive
        ? 'transform: translate3d(8px, 0, 0) rotate(45deg) scale(0.7, 1)'
        : ''};
    ${getBarColor};
  }
  &::after {
    ${({ isActive }) =>
      isActive &&
      'transform: translate3d(8px, 0, 0) rotate(-45deg) scale(0.7, 1)'};
    ${getBarColor};
  }
  ${({ isActive }) => isActive && 'transform: rotate(-180deg)'};
  ${getBarColor};
`

StyledLinesArrowTurn.displayName = 'StyledLinesArrowTurn'
