import React from 'react'
import { Button } from '../button/Button'
import { StyledBox } from '../styledBox/StyledBox'
import { StyledLinesBoring } from './StyledLinesBoring'

const HamburgerBoring = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledBox>
        <StyledLinesBoring {...{ isActive, barColor }} />
      </StyledBox>
    </Button>
  )
}

export default HamburgerBoring
