import Lines from '../Lines/Lines'

const LinesBoring = Lines.extend`
  &::before {
    transition-property: none;
    ${props => (props.isActive ? 'top: 0; opacity: 0;' : '')};
    background-color: ${props => props.barColor || 'black'};
  }
  &::after {
    transition-property: none;
    ${props => (props.isActive ? 'bottom: 0; transform: rotate(-90deg);' : '')};
    background-color: ${props => props.barColor || 'black'};
  }
  transition-property: none;
  ${props => (props.isActive ? 'transform: rotate(45deg)' : '')};
  background-color: ${props => props.barColor || 'black'};
`

export default LinesBoring
