import React from 'react'
import { Button } from '../button/Button'
import { StyledBox } from '../styledBox/StyledBox'
import { StyledLinesCollapse } from './StyledLinesCollapse'

const HamburgerCollapse = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledBox>
        <StyledLinesCollapse {...{ isActive, barColor }} />
      </StyledBox>
    </Button>
  )
}

export default HamburgerCollapse
