import React from 'react'
import { Button } from '../button/Button'
import { StyledBox } from '../styledBox/StyledBox'
import { StyledLinesElastic } from './StyledLinesElastic'

const HamburgerElastic = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledBox>
        <StyledLinesElastic {...{ isActive, barColor }} />
      </StyledBox>
    </Button>
  )
}

export default HamburgerElastic
