import React from 'react'

import Button from '../Button/Button'
import Box from '../Box/Box'
import LinesElastic from './LinesElastic'

const HamburgerElastic = props => (
  <Button onClick={props.toggleButton} buttonColor={props.buttonColor}>
    <Box>
      <LinesElastic isActive={props.isActive} barColor={props.barColor} />
    </Box>
  </Button>
)

export default HamburgerElastic
