import React from 'react'
import { Button } from '../button/Button'
import { StyledBox } from '../styledBox/StyledBox'
import { StyledLinesMinus } from './StyledLinesMinus'

const HamburgerMinus = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledBox>
        <StyledLinesMinus {...{ isActive, barColor }} />
      </StyledBox>
    </Button>
  )
}

export default HamburgerMinus
