import React from 'react'
import { Button } from '../button/Button'
import { StyledBox } from '../styledBox/StyledBox'
import { StyledLinesSlider } from './StyledLinesSlider'

const HamburgerSlider = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledBox>
        <StyledLinesSlider {...{ isActive, barColor }} />
      </StyledBox>
    </Button>
  )
}

export default HamburgerSlider
