import React from 'react'

import Button from '../Button/Button'
import Box from '../Box/Box'
import LinesSlider from './LinesSlider'

const HamburgerSlider = props => (
  <Button onClick={props.toggleButton} buttonColor={props.buttonColor}>
    <Box>
      <LinesSlider isActive={props.isActive} barColor={props.barColor} />
    </Box>
  </Button>
)

export default HamburgerSlider
