import Lines from '../Lines/Lines'

const LinesSlider = Lines.extend`
  &::before {
    top: 10px;
    transition-property: transform, opacity;
    transition-timing-function: ease;
    transition-duration: 0.15s;
    background-color: ${props => props.barColor || 'black'};
    ${props =>
      props.isActive
        ? 'transform: rotate(-45deg) translate3d(-5.71429px, -6px, 0); opacity: 0;'
        : ''};
  }
  &::after {
    top: 20px;
    background-color: ${props => props.barColor || 'black'};
    ${props =>
      props.isActive
        ? 'transform: translate3d(0, -20px, 0) rotate(-90deg);'
        : ''};
  }
  top: 2px;
  background-color: ${props => props.barColor || 'black'};
  ${props =>
    props.isActive ? 'transform: translate3d(0, 10px, 0) rotate(45deg);' : ''};
`

export default LinesSlider
