import React from 'react'
import { Button } from '../button/Button'
import { StyledBox } from '../styledBox/StyledBox'
import { StyledLinesSpin } from './StyledLinesSpin'

const HamburgerSpin = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledBox>
        <StyledLinesSpin {...{ isActive, barColor }} />
      </StyledBox>
    </Button>
  )
}

export default HamburgerSpin
