import React from 'react'

import Button from '../Button/Button'
import Box from '../Box/Box'
import LinesSpin from './LinesSpin'

const HamburgerSpin = props => (
  <Button onClick={props.toggleButton} buttonColor={props.buttonColor}>
    <Box>
      <LinesSpin isActive={props.isActive} barColor={props.barColor} />
    </Box>
  </Button>
)

export default HamburgerSpin
