import React from 'react'

import Button from '../Button/Button'
import Box from '../Box/Box'
import LinesSpring from './LinesSpring'

const HamburgerSpring = props => (
  <Button onClick={props.toggleButton} buttonColor={props.buttonColor}>
    <Box>
      <LinesSpring isActive={props.isActive} barColor={props.barColor} />
    </Box>
  </Button>
)

export default HamburgerSpring
