import React from 'react'
import { Button } from '../button/Button'
import { StyledLinesSpring } from './StyledLinesSpring'

const HamburgerSpring = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledLinesSpring {...{ isActive, barColor }} />
    </Button>
  )
}

export default HamburgerSpring
