import React from 'react'
import { Button } from '../button/Button'
import { StyledBox } from '../styledBox/StyledBox'
import { StyledLinesSqueeze } from './StyledLinesSqueeze'

const HamburgerSqueeze = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledBox>
        <StyledLinesSqueeze {...{ isActive, barColor }} />
      </StyledBox>
    </Button>
  )
}

export default HamburgerSqueeze
