import React from 'react'

import Button from '../Button/Button'
import Box from '../Box/Box'
import LinesStand from './LinesStand'

const HamburgerStand = props => (
  <Button onClick={props.toggleButton} buttonColor={props.buttonColor}>
    <Box>
      <LinesStand isActive={props.isActive} barColor={props.barColor} />
    </Box>
  </Button>
)

export default HamburgerStand
