import React from 'react'

import Button from '../Button/Button'
import BoxThreeDX from './BoxThreeDX'
import LinesThreeDX from './LinesThreeDX'

const HamburgerThreeDX = props => (
  <Button onClick={props.toggleButton} buttonColor={props.buttonColor}>
    <BoxThreeDX>
      <LinesThreeDX isActive={props.isActive} barColor={props.barColor} />
    </BoxThreeDX>
  </Button>
)

export default HamburgerThreeDX
