import React from 'react'

import Button from '../Button/Button'
import BoxThreeDXY from './BoxThreeDXY'
import LinesThreeDXY from './LinesThreeDXY'

const HamburgerThreeDXY = props => (
  <Button onClick={props.toggleButton} buttonColor={props.buttonColor}>
    <BoxThreeDXY>
      <LinesThreeDXY isActive={props.isActive} barColor={props.barColor} />
    </BoxThreeDXY>
  </Button>
)

export default HamburgerThreeDXY
