import Lines from '../Lines/Lines'

const LinesThreeDXYIsActive = `
  background-color: transparent;
  transform: rotateX(180deg) rotateY(180deg) rotateZ(-180deg);
`

const LinesThreedXY = Lines.extend`
  ${props => (props.isActive ? LinesThreeDXYIsActive : '')};
  transition: transform 0.15s cubic-bezier(0.645, 0.045, 0.355, 1),
    background-color 0s 0.1s cubic-bezier(0.645, 0.045, 0.355, 1);

  &::before,
  &::after {
    background-color: ${props => props.barColor || 'black'};
    transition: transform 0s 0.1s cubic-bezier(0.645, 0.045, 0.355, 1);
  }

  &::before {
    ${props =>
      props.isActive ? 'transform: translate3d(0, 10px, 0) rotate(45deg)' : ''};
  }

  &::after {
    ${props =>
      props.isActive
        ? 'transform: translate3d(0, -10px, 0) rotate(-45deg)'
        : ''};
  }
`

export default LinesThreedXY
