import { StyledLines } from '../styledLines/StyledLines'
import { getBarColor } from '../functions/'

const LinesThreeDXYIsActive = `
  background-color: transparent;
  transform: rotateX(180deg) rotateY(180deg) rotateZ(-180deg);
`

export const StyledLinesThreeDXY = StyledLines.extend`
  ${({ isActive }) => isActive && LinesThreeDXYIsActive};
  transition: transform 0.15s cubic-bezier(0.645, 0.045, 0.355, 1),
    background-color 0s 0.1s cubic-bezier(0.645, 0.045, 0.355, 1);

  &::before,
  &::after {
    ${getBarColor};
    transition: transform 0s 0.1s cubic-bezier(0.645, 0.045, 0.355, 1);
  }

  &::before {
    ${({ isActive }) =>
      isActive && 'transform: translate3d(0, 10px, 0) rotate(45deg)'};
  }

  &::after {
    ${({ isActive }) =>
      isActive && 'transform: translate3d(0, -10px, 0) rotate(-45deg)'};
  }
`

StyledLinesThreeDXY.displayName = 'StyledLinesThreeDXY'
