import React from 'react'

import Button from '../Button/Button'
import BoxThreeDY from './BoxThreeDY'
import LinesThreeDY from './LinesThreeDY'

const HamburgerThreeDY = props => (
  <Button onClick={props.toggleButton} buttonColor={props.buttonColor}>
    <BoxThreeDY>
      <LinesThreeDY isActive={props.isActive} barColor={props.barColor} />
    </BoxThreeDY>
  </Button>
)

export default HamburgerThreeDY
