import React from 'react'
import { Button } from '../button/Button'
import { StyledBox } from '../styledBox/StyledBox'
import { StyledLinesVortex } from './StyledLinesVortex'

const HamburgerVortex = props => {
  const { isActive, barColor } = props

  return (
    <Button {...props}>
      <StyledBox>
        <StyledLinesVortex {...{ isActive, barColor }} />
      </StyledBox>
    </Button>
  )
}

export default HamburgerVortex
