import styled from 'styled-components'

const LinesCommon = `
width: 40px;
height: 4px;
border-radius: 4px;
position: absolute;
transition-property: transform;
transition-duration: 0.15s;
transition-timing-function: ease;
`

export const StyledLines = styled.span`
  display: block;
  top: 50%;
  margin-top: -2px;
  background-color: ${props => props.barColor || 'black'};
  ${LinesCommon};
  &::before,
  &::after {
    background-color: ${props => props.barColor || 'black'};
    ${LinesCommon};
  }
  &::before,
  &::after {
    content: '';
    display: block;
  }
  &::before {
    top: -10px;
  }
  &::after {
    bottom: -10px;
  }
`

StyledLines.displayName = 'StyledLines'
